    private void jbsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbsimpanActionPerformed
        // TODO add your handling code here:
        String  jurusan, jeniskelamin;
        jurusan = (String)cbjurusan.getSelectedItem();
        if(rblaki.isSelected()){
            jeniskelamin = "Laki-Laki";
        }
        else {
            jeniskelamin = "perempuan";
        }
        
        String nama = tfnama.getText();
        String tempatlahir = tftel.getText();
        String tanggallahir = tftal.getText();
        String alamat = tfalamat.getText(); 
        String telpon = tftelp.getText();
        String email = tfemail.getText();
        String asalsekolah = tfsekolah.getText();
        String namaayah = tfnamaayah.getText();
        String namaibu = tfnamaibu.getText();
        
        if (!jCheckBox1.isSelected()) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Silakan centang checkbox terlebih dahulu.", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return;
        }
     
        if (!rblaki.isSelected() && !rbperempuan.isSelected()) {
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Silakan pilih jenis kelamin.", 
            "Peringatan", 
            javax.swing.JOptionPane.WARNING_MESSAGE);
        return; 
        }
        
        if (cbjurusan.getSelectedIndex() == 0) { 
        javax.swing.JOptionPane.showMessageDialog(this, 
            "Silakan pilih jurusan.", 
            "Peringatan", 
            javax.swing.JOptionPane.ERROR_MESSAGE);
        return; 
        }
        
        frame2 data = new frame2(nama, tempatlahir, tanggallahir, alamat, jeniskelamin, telpon, email,
                      asalsekolah, namaayah, namaibu, jurusan);
        data.setVisible(true);
        this.dispose();
        
    }//GEN-LAST:event_jbsimpanActionPerformed

