        void setData(String Nama, String Alamat, String Nomor, String Ibu, String Ayah, String Ttl, String Prodi, String gender, String Fakultas) {
              jnama.setText(Nama+"\n");
              jalamat.setText(Alamat+"\n");
              jnomor.setText(Nomor+"\n");
              jibu.setText(Ibu+"\n");
              jayah.setText(Ayah+"\n");
              jttl.setText(Ttl+"\n");
              jprodi.setText(Prodi+"\n");
              jgender.setText(gender+"\n");
              jfakultas.setText(Fakultas+"\n");
}
