    void setData(String Nama, String Alamat, String Asal, String Tanggal, String No, String jlJenis, String fakultas, boolean isEditable) {
    tfNama.setText(Nama);
    tfAlamat.setText(Alamat);
    tfAsal.setText(Asal);
    tfTanggal.setText(Tanggal);
    tfNo.setText(No);
    tfFakultas.setText(fakultas);
    tfJenis.setText(jlJenis);
    
    // Nonaktifkan field jika isEditable bernilai false
    tfNama.setEditable(isEditable);
    tfAlamat.setEditable(isEditable);
    tfAsal.setEditable(isEditable);
    tfTanggal.setEditable(isEditable);
    tfNo.setEditable(isEditable);
    tfFakultas.setEditable(isEditable);
    tfJenis.setEditable(isEditable);
    }
