    private void SimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SimpanActionPerformed
//        if (NamaSiswa.getText().equals("") || NI.getText().equals("") || nisn.getText().equals("") || ttl.getText().equals("") || jeniskelamin1.getText().equals("") ||jeniskelamin2.getText().equals("")|| agama.getText().equals("") || notelp.getText().equals("") || asalsekolah.getText().equals("") || jurusan.getSelectedItem().equals("") || namaayah.getText().equals("") || namaibu.getText().equals("") || Telepon.getText().equals("") || workayah.getText().equals("")){
//        JOptionPane.showMessageDialog(null, "Data tidak boleh ada yang kosong");}
        if (!box1.isSelected()){
            JOptionPane.showMessageDialog(null, "Diusahakan di centang ya");
        }
        else{
            Frame2 frame2 = new Frame2();
            frame2.f1.setText(NamaSiswa.getText());
            frame2.f14.setText(NI.getText());
            frame2.f2.setText(nisn.getText());
            frame2.f3.setText(ttl.getText());
            
            String jenisKelamin = getSelectedButtonText(buttonGroup);
            if (jenisKelamin != null) {
            frame2.f4.setText(jenisKelamin); 
            } else {
            frame2.f4.setText("Tidak dipilih");
        }
            frame2.f5.setText(agama.getText());
            frame2.f6.setText(notelp.getText());
            frame2.f7.setText(asalsekolah.getText());
            frame2.f8.setText(jurusan.getSelectedItem().toString());
            frame2.f10.setText(namaayah.getText());
            frame2.f11.setText(namaibu.getText());
            frame2.f12.setText(alamatAyah.getText());
            frame2.f13.setText(Telepon.getText());
            frame2.f9.setText(workayah.getText());
            frame2.setVisible(true);
            this.dispose();
        }
    }//GEN-LAST:event_SimpanActionPerformed
    public String getSelectedButtonText(ButtonGroup group) {
    for (Enumeration<AbstractButton> buttons = group.getElements(); 
        buttons.hasMoreElements();) {
        AbstractButton button = buttons.nextElement();
        if (button.isSelected()) {
            return button.getText();  
        }
    }
    return null;  
}
