    private void simpanbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simpanbtnActionPerformed
        if (boxvalidasi.isSelected()){
            String nama, nim, prodi, alamat, nomor, semester, gender = null, ip;

            nama = Nama.getText();
            nim = NIM.getText();
            prodi = Prodi.getText();
            ip = IP.getText();
            alamat = Alamat.getText();
            nomor = Nomor.getText();
            semester = (String)combosemester.getSelectedItem();
            if(radiolaki.isSelected()){
                gender = "Laki - laki";
            }else if(radioperempuan.isSelected()){
                gender = "Perempuan";
            }else{
                gender = "";
            }
            if ("".equals(nomor) || "".equals(nama) || "".equals(nim) || "".equals(prodi) || "".equals(ip) || "".equals(alamat)
                    || "pilih".equals(semester) || "".equals(gender) ){
                javax.swing.JOptionPane.showMessageDialog(this, "Lengkapi Datanya");
            }else{
                jPanel3.setVisible(true);
                jPanel2.setVisible(false);
                TNama.setText(nama);
                TNIM.setText(nim);
                TProdi.setText(prodi);
                TSemester.setText(semester);
                TIP.setText(ip);
                TGender.setText(gender);
                TAlamat.setText(alamat);
                TNomor.setText(nomor);
            }
        }
        else{
            javax.swing.JOptionPane.showMessageDialog(this, "Pastikan data yang dimasukkan benar");
        }
        
    }//GEN-LAST:event_simpanbtnActionPerformed

    private void okebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okebtnActionPerformed
        javax.swing.JOptionPane.showMessageDialog(this, "Terimakasih sudah mendaftar");
        jPanel3.setVisible(false);
        jPanel2.setVisible(true);
    }//GEN-LAST:event_okebtnActionPerformed

    private void resetbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resetbtnActionPerformed
        Nama.setText("");
        NIM.setText("");
        Prodi.setText("");
        IP.setText("");
        Alamat.setText("");
        Nomor.setText("");
        radiolaki.setSelected(false);
        radioperempuan.setSelected(false);
        combosemester.setSelectedItem("pilih");
        boxvalidasi.setSelected(false);
        
    }//GEN-LAST:event_resetbtnActionPerformed

