    private void tfcashActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tfcashActionPerformed
        // Pastikan kedua field (tftotal dan tfcash) tidak kosong
        if (!tftotal.getText().isEmpty() && !tfcash.getText().isEmpty()) {
            // Mengubah nilai dari field tftotal dan tfcash ke tipe data integer
            int totalHarga = Integer.parseInt(tftotal.getText());
            int bayar = Integer.parseInt(tfcash.getText());

            // Cek apakah uang yang dibayar cukup atau lebih dari total harga
            if (bayar >= totalHarga) {
                // Hitung kembalian
                int kembalian = bayar - totalHarga;
                // Menampilkan kembalian di field tfkembali
                tfkembali.setText(String.valueOf(kembalian));
            } else {
                // Jika uang tidak cukup, tampilkan pesan di field tfkembali
                tfkembali.setText("Uang tidak cukup");
            }
        }
    }//GEN-LAST:event_tfcashActionPerformed

    private void btotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btotalActionPerformed
        // Periksa apakah menu sudah dipilih
        if (!pastry.isSelected() && !flufy.isSelected() && !croissant.isSelected() && !cupcake.isSelected() && !dessert.isSelected() && !makaron.isSelected() && !pancake.isSelected() && !donnut.isSelected()) {
            JOptionPane.showMessageDialog(null, "Silakan pilih menu terlebih dahulu!", "Peringatan", JOptionPane.WARNING_MESSAGE);
            return;
        }

        // Periksa apakah toping sudah dipilih
        if (!rbno.isSelected() && !rbchoco.isSelected() && !rbvanilla.isSelected() && !rbtiramisu.isSelected()) {
            JOptionPane.showMessageDialog(null, "Silakan pilih topping terlebih dahulu!", "Peringatan", JOptionPane.WARNING_MESSAGE);
            return;
        }
        // Periksa apakah jumlah telah diisi dan lebih dari 0
        if (tfjumlah.getText().isEmpty() || tfjumlah.getText().equals("0")) {
            JOptionPane.showMessageDialog(null, "Silakan masukkan jumlah terlebih dahulu!", "Peringatan", JOptionPane.WARNING_MESSAGE);
            return;
        }

        // Inisialisasi total harga
        int totalPrice = 0;

        // Periksa radio button menu yang dipilih dan tambahkan harga ke total harga
        if (pastry.isSelected()) {
            totalPrice += 30000;
        } else if (flufy.isSelected()) {
            totalPrice += 35000;
        } else if (croissant.isSelected()) {
            totalPrice += 30000;
        } else if (cupcake.isSelected()) {
            totalPrice += 20000;
        } else if (dessert.isSelected()) {
            totalPrice += 40000;
        } else if (makaron.isSelected()) {
            totalPrice += 25000;
        } else if (pancake.isSelected()) {
            totalPrice += 25000;
        } else if (donnut.isSelected()) {
            totalPrice += 15000;
        }

        // Periksa radio button topping yang dipilih dan tambahkan harga ke total harga
        if (rbno.isSelected()) {
            totalPrice += 0; // Tidak ada biaya tambahan untuk topping tidak ada
        } else if (rbchoco.isSelected() || rbvanilla.isSelected() || rbtiramisu.isSelected()) {
            totalPrice += 5000; // Topping yang sama, biaya tambahan sama
        }

        // Kalikan total harga dengan jumlah yang dibeli
        int jumlahBeli = Integer.parseInt(tfjumlah.getText());
        totalPrice *= jumlahBeli;

        // Terapkan diskon 5% jika total harga lebih dari 100.000
        if (totalPrice > 100000) {
            totalPrice = (int) (totalPrice * 0.95); // Diskon 5%
        }
        // Update total text field
        tftotal.setText(String.valueOf(totalPrice));
    }//GEN-LAST:event_btotalActionPerformed

    private void tambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tambahActionPerformed
        int jumlah = Integer.parseInt(tfjumlah.getText());
        // Tambah nilai jumlah pesanan
        jumlah++;
        // Perbarui label dengan nilai yang baru
        tfjumlah.setText(String.valueOf(jumlah));
    }//GEN-LAST:event_tambahActionPerformed

