    private void pigeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pigeActionPerformed
    // Reset semua warna menjadi putih terlebih dahulu
    dema.setForeground(java.awt.Color.WHITE);
    para.setForeground(java.awt.Color.WHITE);
    sim.setForeground(java.awt.Color.WHITE);
    ome.setForeground(java.awt.Color.WHITE);
    ceti.setForeground(java.awt.Color.WHITE);
    asa.setForeground(java.awt.Color.WHITE);
    cata.setForeground(java.awt.Color.WHITE);
    met.setForeground(java.awt.Color.WHITE);
    xit.setForeground(java.awt.Color.WHITE);
    bod.setForeground(java.awt.Color.WHITE);
    neu.setForeground(java.awt.Color.WHITE);
    meth.setForeground(java.awt.Color.WHITE);

    // Ubah warna teks sesuai pilihan yang dipilih
    switch(pige.getSelectedIndex()) {
        case 1:
            deskripsi.setText("Demacolin : Obat ini digunakan untuk meringankan gejala flu "
                    + "seperti demam, sakit kepala, hidung tersumbat, dan bersin-bersin");
            dema.setForeground(java.awt.Color.YELLOW); // Ubah ke kuning
            break;
        case 2:
            deskripsi.setText("Paracetamol : Obat ini digunakan untuk meredakan demam dan nyeri");
            para.setForeground(java.awt.Color.YELLOW);
            break;
        case 3:
            deskripsi.setText("Simvastatin : Obat ini merupakan obat penurun kolesterol");
            sim.setForeground(java.awt.Color.YELLOW);
            break;
        case 4:
            deskripsi.setText("Omeprazole : Obat ini untuk mengatasi asam lambung");
            ome.setForeground(java.awt.Color.YELLOW);
            break;
        case 5:
            deskripsi.setText("Cetirizine : Obat ini digunakan untuk meredakan gejala gatal");
            ceti.setForeground(java.awt.Color.YELLOW);
            break;
        case 6:
            deskripsi.setText("Asam Mefenamat : Obat ini digunakan untuk nyeri ringan sampai sedang seperti "
                    + "sakit kepala, sakit gigi, nyeri haid");
            asa.setForeground(java.awt.Color.YELLOW);
            break;
        case 7:
            deskripsi.setText("Cataflam : Obat ini digunakan untuk pereda nyeri mulai dari nyeri gigi, "
                    + "nyeri otot, nyeri sendi, hingga nyeri haid.");
            cata.setForeground(java.awt.Color.YELLOW);
            break;
        case 8:
            deskripsi.setText("Metformin : Obat ini digunakan untuk menurunkan kadar gula darah pada "
                    + "penderita diabetes tipe 2.");
            met.setForeground(java.awt.Color.YELLOW);
            break;
        case 9:
            deskripsi.setText("Xitrol : Obat ini digunakan untuk mengatasi infeksi bakteri pada mata "
                    + "yang menyebabkan mata merah");
            xit.setForeground(java.awt.Color.YELLOW);
            break;
        case 10:
            deskripsi.setText("Bodrex : Obat ini digunakan untuk meringankan sakit kepala, "
                    + "sakit gigi, dan menurunkan demam.");
            bod.setForeground(java.awt.Color.YELLOW);
            break;
        case 11:
            deskripsi.setText("Neuralgin : Obat ini digunakan untuk mengatasi nyeri sendi/otot");
            neu.setForeground(java.awt.Color.YELLOW);
            break;
        case 12:
            deskripsi.setText("Methylprednisolone : Obat ini digunakan untuk meredakan radang");
            meth.setForeground(java.awt.Color.YELLOW);
            break;
    }

    }//GEN-LAST:event_pigeActionPerformed

